import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Style from "./login.module.scss"

export default function Login() {
    const [isShowPassword, setShowPassword] = useState(false);
    const [isPassword, setPassword] = useState ('');

    const handlePasswordChange = (e) => {
        setPassword(e.target.value)
    }
    const togglePasswordVisibility = () => {
        setShowPassword(!isShowPassword)
    }
  return (
    <div className={Style.main}>
      <Form className={Style.container__form}>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Endereço de e-mail</Form.Label>
          <Form.Control type="email" placeholder="Insira seu e-mail" />
          <Form.Text className="text-muted">
            Nunca compartilhe seu endereço de e-mail com outros.
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Senha</Form.Label>
          <Form.Control type={isShowPassword ? "text" : "password"} value={isPassword} onChange={handlePasswordChange} placeholder="Insira sua senha" />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicCheckbox">
          <Form.Check type="checkbox" checked={isShowPassword} onC label="Check me out" />
        </Form.Group>
        <Button variant="primary" type="submit">
          Login
        </Button>
      </Form>
    </div>
  );
}
