import React, { useState } from "react";
import Carousel from "react-bootstrap/Carousel";
import images from "components/json/imagens.json";

export default function Carrossel() {
  const [imagens] = useState(images);

  const renderImages = () => {
    return imagens.imagem.map((imagem, index) => {
      return (
        <Carousel.Item key={index}>
          <img
            className="d-block w-100"
            src={imagem.imagem}
            alt={imagem.nome}
          />
        </Carousel.Item>
      );
    });
  };

  return (
    <Carousel interval={1000}>
      {renderImages()}
    </Carousel>
  );
}
