import React from "react";
import Aside from "components/aside";

export default function CheckboxFilter({
  style,
  veiculosFiltrados,
  setVeiculoFiltrado,
  automoveis,
  filtrarVeiculos,
}) {
  return (
    <div className={style.filtro}>
      <label className={style.borda__filtro}>
        <h3 className={style.titulo__filtro}>Marca</h3>
        {automoveis.map((marca, index) => (
          <button
            key={marca + index}
            className={`${style.filtro__botao} ${
              veiculosFiltrados.indexOf(marca) !== -1
                ? style.filtro__botaoSelecionado
                : ""
            }`}
            onClick={(e) => {
              if (e.target.checked) {
                filtrarVeiculos(marca);
              } else {
                const resultado = veiculoFiltrado.filter(
                  (item) => item.marca !== marca
                );
                filtrarVeiculos("ResetViews", resultado);
              }}}
          >
            {marca}
          </button>
        ))}
      </label>
    </div>
  );
}
