// import React from "react";
// import Burger from "components/menuBurger";
// import { Outlet } from "react-router-dom";

import React, { useState } from "react";
import QRCode from "qrcode.react";

function Home({ open, setOpen }) {
  const dados = ["Nome", "Telefone", "Email"];
  const [isDados, setDados] = useState(dados)
  // const [nome, setNome] = useState("");
  // const [email, setEmail] = useState("");
  // const [telefone, setTelefone] = useState("");
  const [exibirQRCode, setExibirQRCode] = useState(false);

  function handleGerarQRCode() {
    setExibirQRCode(true);
  }
  return (
    <>
      <div>
        <label>
          Nome:
          <input
            type="text"
            value={isDados}
            onChange={(e) => setDados(e.target.value)}
          />
        </label>
        <label>
          Email:
          <input
            type="text"
            value={isDados}
            onChange={(e) => setDados(e.target.value)}
          />
        </label>
        <label>
          Telefone:
          <input
            type="text"
            value={isDados}
            onChange={(e) => setDados(e.target.value)}
          />
        </label>
        <button onClick={handleGerarQRCode}>Gerar QR Code</button>
        {exibirQRCode && (
          <QRCode
            value={`Nome: ${nome}, Email: ${email}, Telefone: ${telefone}`}
          />
        )}
      </div>
      {/* <Burger open={open} setOpen={setOpen} />
      <Outlet /> */}
    </>
  );
}
export default Home;
