import Search from "../img/loupe.png";
// import Logout from "../img/Logout.png";
import Logo from "../img/ToraLogo.png";
import styled from "styled-components";
import Button from "../buttons";
import TextField from "../textField";

const ImgContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  width: 100%;
  margin: 0 40px;

  @media only screen and (max-width: 768px) {
    flex-flow: column nowrap;
    display: flex;
    align-items: center;
  }
`;

const Img = styled.img`
  margin-top: 5px;
  width: 20px;
  height: 20px;
  display: flex;
  align-items: center;
  cursor: pointer;
  opacity: 0.7;
  margin: 3px 45px 0 3px;
  &:hover {
    opacity: 1;
  }
  &:active {
    opacity: 0.5;
  }
`;



const SearchBar = styled.div`
  display: flex;
  border: none;
  border-radius: 20px;
  width: 16rem;
  @media only screen and (max-width: 768px) {
    display: flex;
    align-items: center;
    width: 13rem;
    margin-bottom: 50px;
  }
`;
const LogoContainer = styled.div`
  display: flex;

  @media only screen and (max-width: 768px) {
    display: flex;
    flex-flow: column;
    align-items: center;
  }
`;
const Title = styled.p`
  padding: 0 15px;
  font-size: 20px;
  font-weight: 100;
`;
const ImgLogo = styled.img`
  width: 30px;
  height: 30px;
  display: flex;
  justify-content: space-between;
  cursor: pointer;
  margin: 20px 0px 0 32px;
  opacity: 0.7;
  &:hover {
    opacity: 1;
  }
  &:active {
    opacity: 0.5;
  }

  @media only screen and (max-width: 768px) {
    padding: 20px;
    margin-left: 0;
  }
`;
const Icons = (props) => {
  return (
    <>

      <ImgContainer>
        <SearchBar>
          <TextField placeholder="Buscar" />
          <Img src={Search} alt="Lupa de pesquisa"></Img>
        </SearchBar>
        <Button>Login</Button>
      </ImgContainer>
    </>
  );
};
export default Icons;
