import Search from "../img/loupe.png";
import styled from "styled-components";
import Button from "../buttons";
import TextField from "../textField";

const ImgContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  width: 100%;
  margin: 0 40px;

  @media only screen and (max-width: 768px) {
    flex-flow: column nowrap;
    display: flex;
    align-items: center;
  }
`;
const Img = styled.img`
  margin-top: 5px;
  width: 20px;
  height: 20px;
  display: flex;
  align-items: center;
  cursor: pointer;
  opacity: 0.7;
  margin: 3px 45px 0 3px;
  &:hover {
    opacity: 1;
  }
  &:active {
    opacity: 0.5;
  }
`;
const SearchBar = styled.div`
  display: flex;
  border: none;
  border-radius: 20px;
  width: 16rem;
  @media only screen and (max-width: 768px) {
    display: flex;
    align-items: center;
    width: 13rem;
    margin-bottom: 50px;
  }
`;

const Icons = (props) => {
  return (
    <>

      <div className={Style}>
        <SearchBar>
          <TextField placeholder="Buscar" />
          <Img src={Search} alt="Lupa de pesquisa"></Img>
        </SearchBar>
        <Button>Login</Button>
      </div>
    </>
  );
};
export default Icons;
