import styled from "styled-components";
import Style from ""
import Icons from "../icons";
import Logo from "../img/ToraLogo.png";


const HeaderContainer = styled.header`
  display: flex;
  position: fixed;
  top: 0;
  justify-content: space-around;
  width: 100vw;
  height: 50px;
  background: linear-gradient(65deg, #00652e 35%, #00652ee1);
  align-items: center;
  color: #fff;
  flex-flow: row nowrap;
  
  @media only screen and (max-width: 768px) {
    flex-flow: column nowrap;
    background-color: #185c3e;
    position: fixed;
    transform: ${({ open }) => (open ? "translateX(100%)" : "translateX(0)")};
    top: 0;
    right: 0;
    height: 38rem;
    width: 23rem;
    padding-top: 3.5rem;
    transition: transform 0.3s ease-in-out;
`;
const LogoContainer = styled.div`
  display: flex;

`;
const Title = styled.p`
  padding: 0 15px;
  font-size: 20px;
  font-weight: 100;
`;
const ImgLogo = styled.img`
  width: 30px;
  height: 30px;
  display: flex;
  justify-content: space-between;
  cursor: pointer;
  margin: 20px 0px 0 32px;
  opacity: 0.7;
  &:hover {
    opacity: 1;
  }
  &:active {
    opacity: 0.5;
  }

`;
const Header = ({ open }) => {
  return (
    <>
      <div className={Style.header__container} open={open} >
      <div className={Style.logo__container}>
        <img src={Logo} alt="Logomarca Tora Transportes" />
        <div className={Style.title}>
          <strong>Tora</strong>
          Seminovos
        </div >
      </div>
        <Icons />
      </div>
    </>
  );
};
export default Header;
