import React, { useState } from 'react'
import Form from "react-bootstrap/Form";
import style from "./aside.module.scss";


const Aside = ({ marca, filtrarVeiculos, veiculoFiltrado }) => {
  const [isAtivo, setIsAtivo] = useState(false);

  function handleFiltroChange(e) {
    setIsAtivo(e.target.checked);
  }
  return (
    <div className={style.container}>
      <label>
        <Form>
          <Form.Check
            type="switch"
            id="custom-switch"
            className={style.checkbox}
            onChange={(e) => {
              if (e.target.checked) {
                filtrarVeiculos(marca);
              } else {
                const resultadoFiltro = veiculoFiltrado.filter(
                  (item) => item.marca !== marca
                );
                filtrarVeiculos("ResetViews", resultadoFiltro);
              }
            }}
          />
        </Form>
        {marca}
      </label>
    </div>
  );
};
export default Aside;
