export default function CheckboxFilter({ style, veiculosFiltrados, setVeiculosFiltrados, automoveis, filtrarVeiculosPorMarca }) {
  return (
    <div className={style.filtro}>
      <label className={style.borda__filtro}>
        <h3 className={style.titulo__filtro}>
        Marca
        </h3>
        {automoveis.map((marca, index) => (
          <button
            key={marca + index}
            className={`${style.filtro__botao} ${
              veiculosFiltrados.indexOf(marca) !== -1 ? style.filtro__botaoSelecionado : ""
            }`}
            onClick={() => filtrarVeiculosPorMarca(marca, veiculosFiltrados)}
          >
            {marca}
          </button>
        ))}
      </label>
    </div>
  );
}
