import { Buttons } from "components/formFields";
import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";

export default function QRCodeModal({ handleQRCodeGenerator, isShowQRCode, QRCode }) {

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>
      <Buttons
        variant="outline-dark"
        type={"button"}
        onClick={handleShow}
        name={"Gerar QR Code"}
      />

      <Modal show={show} onHide={handleClose} handleQR={handleQRCodeGenerator} animation={false}>
        <Modal.Header closeButton>
          <Modal.Title>Resumo da venda</Modal.Title>
        </Modal.Header>
        <Modal.Body>{isShowQRCode && (<QRCode value />)}</Modal.Body>
      </Modal>
    </>
  );
}
